actionConfiguration.body:
{
	"query": "
		query User_GetStudentListWithFilter($limit: Int = 10, $offset: Int = 0, $order_by: users_order_by! = {created_at: desc}, $where: users_bool_exp! = {}) {
  users(where: $where, limit: $limit, offset: $offset, order_by: [$order_by]) {
    user_id
    name
    full_name_phonetic
    email
    phone_number
    country
    last_login_date
    resource_path
    student {
      contact_preference
    }
  }
}

	",
"variables": {
		"limit": 10,
		"offset": {{Table1.pageOffset}},
		"where": {
			"_or": [
				{
					"name": {}
				},
				{
					"full_name_phonetic": {}
				},
				{
					"user_id": {
						"_in": []
					}
				}
			],
			"last_login_date": {},
			"user_access_paths": {
				"location_id": {
					"_in": [
						"01GA9XFTGKZVZEWXZCY1A4BG3G"
					]
				}
			},
			"user_group": {
				"_eq": "USER_GROUP_STUDENT"
			},
			"user_id": {}
		}
	}
}
