actionConfiguration.body:
{
	query: '
				fragment LessonAttrs on lessons {
            lesson_id
            start_time
            end_time
            scheduling_status
            center_id
            lessons_teachers {
              teacher_id
              teacher_name
            }
            teaching_medium
            teaching_method
            course_id
            class_id
          }
		query Architecture_FutureLessonList($limit: Int = 10, $offset: Int =
          0, $time: timestamptz) {
		 lessons(limit: $limit, offset: $offset, where: {start_time: {_gte: $time}}, order_by: 	      {start_time: asc, end_time: asc, lesson_id: asc}) {
              ...LessonAttrs
            }
            lessons_aggregate {
              aggregate {
                count
              }
            }
		}
	',
	variables: {
		"time": {{this.params.time}},
		"limit": {{this.params.limit}},
		"offset": {{this.params.offset}}
	}
}
