async () => {
  const mappingEnv = {
    "staging": "stag",
    "uat": "uat",
    "master": "prod"
  };
  let checkEnvAndOrg = function (env, orgID, strategies) {
    let found = false;
    for (let i = 0; i < strategies.length; i++) {
      if (strategies[i].name == 'strategy_environment') {
        const environments = strategies[i].parameters.environments.split(",");
        for (let e = 0; e < environments.length; e++) {
          if (environments[e] == env) {
            found = true;
            break;
          }
        }
      }
      if (strategies[i].name == 'strategy_organization' && orgID != undefined && found) {
        found = false;
        const organizations = strategies[i].parameters.organizations.split(",");
        for (let o = 0; o < organizations.length; o++) {
          if (organizations[o] == orgID) {
            found = true;
            break;
          }
        }
      }
    }
    return found;
  };
  const env = mappingEnv[appsmith.URL.queryParams["branch"]] ? mappingEnv[appsmith.URL.queryParams["branch"]] : "stag";
  const orgID = appsmith.URL.queryParams["organization"];
  console.log(orgID);
  const results = await Unleash_Features.run();
  const features = results.features.filter(feature => {
    return feature.enabled && checkEnvAndOrg(env, orgID, feature.strategies);
  });
  console.log(features.length);
  return;
}

